// Install packages:
// npm install react-slick slick-carousel

import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

export default function MyCarousel({ items }) {
  const settings = {
    dots: true,               // show navigation dots
    infinite: true,           // loop slides
    speed: 1000,              // transition speed (1s fade)
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,           // auto slide on
    autoplaySpeed: 5000,      // 5 seconds between slides
    fade: true,               // ðŸ‘ˆ smooth fade transition
    pauseOnHover: true,       // pause if hovered
    arrows: false             // hide arrows for clean look
  };

  return (
    <Slider {...settings}>
      {items.map((item, i) => (
        <div key={i} className="text-center">
          <img 
            src={item.imageUrl} 
            alt={item.title} 
            style={{
              width: "100%",
              height: "400px",
              objectFit: "cover",
              borderRadius: "10px"
            }} 
          />
          <h3 className="mt-3">{item.title}</h3>
        </div>
      ))}
    </Slider>
  );
}
