import {NavBar} from '../CoustomNavBar/NavBar';

import MyCarousel from '../Carousal/carousal';

import React, { useState, useEffect } from "react";

import WatherAPIdata from '../FeatchingData/FeatchingDataAPI'; 

export default function Home() {

  const slidesData = [
  { imageUrl: 'asert/firstban.jpg', title: 'Slide 1' },
  { imageUrl: 'asert/secondban.jpg', title: 'Slide 2' },
   { imageUrl: 'asert/thirdadx.jpg', title: 'Slide 3'},
  { imageUrl: 'asert/fourpl.jpg', title: 'Slide 4'}
  // dynamically fetched or updated
];

const [color, setColor] = useState("lightblue");

  useEffect(() => {
    document.body.style.backgroundColor = color;
  }, [color]); // Update body background when color changes


 return(
    <div className="container-fluid">
    
    <div className="text-center p-5">
      <h2>Dynamic Body Background</h2>
      <button onClick={() => setColor("lightgreen")} className="btn btn-success m-2">Green</button>
      <button onClick={() => setColor("lightcoral")} className="btn btn-danger m-2">Red</button>
      <button onClick={() => setColor("lightblue")} className="btn btn-primary m-2">Blue</button>
    </div>

      <h2>Home Page </h2>
       <NavBar BrandTitle="MyApp" MenuItems={["Home","Service","Contect","About","Help"]}/>  
       <MyCarousel items={slidesData} />
       <WatherAPIdata/>

    </div>
  )
  
}
