import React, { useState, useEffect } from "react";
export function BodyBg() {

const [color, setColor] = useState("lightblue");

  useEffect(() => {
    document.body.style.backgroundColor = color;
  }, [color]); // Update body background when color changes



  return(
    <div className="text-center p-1">
      <button onClick={() => setColor("lightgreen")} className="btn btn-success m-2">Green</button>
      <button onClick={() => setColor("lightcoral")} className="btn btn-danger m-2">Red</button>
      <button onClick={() => setColor("lightblue")} className="btn btn-primary m-2">Blue</button>
    </div>
  )
  
}
