import React, { useState, useEffect } from "react";
export function BodyBg() {

const [color, setColor] = useState("lightblue");

  useEffect(() => {
    document.body.style.backgroundColor = color;
  }, [color]); // Update body background when color changes



 return (
    <div className="text-center p-3">
      <h4 className="text-white mb-3">ðŸŒˆ Choose Background Gradient</h4>
      <button
        onClick={() => setColor("gradient1")}
        className="btn btn-success m-2"
      >
        Green-Pink
      </button>
      <button
        onClick={() => setColor("gradient2")}
        className="btn btn-primary m-2"
      >
        Blue Sky
      </button>
      <button
        onClick={() => setColor("gradient3")}
        className="btn btn-warning m-2"
      >
        Sunset
      </button>
      <button
        onClick={() => setColor("gradient4")}
        className="btn btn-info m-2"
      >
        Ocean
      </button>
      <button
        onClick={() => setColor("gradient5")}
        className="btn btn-danger m-2"
      >
        Peach Dream
      </button>
    </div>
  );
  
}
